import React, { useEffect } from "react";
import { fetchTeam,deleteTeam,addPlayers } from "../action/index";
import { connect } from "react-redux";
import Modal from "@netojose/react-modal";
const Team = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const openModal = useCallback(() => setIsOpen(true), []);
  const closeModal = useCallback(() => setIsOpen(false), []);
  const [term, setTerm] = useState("");
  useEffect(() => {
    props.fetchTeam();
  }, []);

  const addPlayer = (id,pla)=>{
    var mani = props.t.player.filter(ele=>ele._id==id)[0]
    var players = mani.player
          players.push(pla)
    var obj = {
      "id":mani._id,
      "title":mani.title,
      "player":mani.player
  }
    console.log(obj)
    closeModal()
  }
  return (
    <div>
      {props.t.team.map(ele=>{
        return(
          <div id={ele._id}>
            <p>{ele.title} <button onClick={()=>props.deleteTeam(ele._id)}>Delete</button>

            <button onClick={openModal} >Add Player</button>
            <Modal isOpen={isOpen} onRequestClose={closeModal}>
              <input
                      className="main-input-style"
                      type="text"
                      value={term}
                      onChange={(event)=>setTerm(event.target.value)}
                      placeholder={`Enter Team Name`}
                  /><br/>
                <Button onClick={()=>addPlayer(ele._id)}>Add Team</Button> <Button onClick={closeModal}>Cancel</Button>
              </Modal>
            </p>
          </div>
        )
      })}
    </div>
  );
};

function mapStateToProps(state) {
  console.log(state)
  return {
    t: state.team,
  };
}

export default connect(mapStateToProps, { fetchTeam,deleteTeam, addPlayers })(Team);
