import React, { useEffect ,useState, useCallback} from "react";
import { fetchTeam,deleteTeam,deletePlayers } from "../action/index";
import { connect } from "react-redux";
import Button from '@material-ui/core/Button';
import Modal from "@netojose/react-modal";
import Box from '@material-ui/core/Box';
const Team = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const openModal = useCallback(() => setIsOpen(true), []);
  const closeModal = useCallback(() => setIsOpen(false), []);
  const [term, setTerm] = useState("");
  useEffect(() => {
    props.fetchTeam();
  }, []);

  const addPlayer = (id)=>{
    var mani = props.t.team.filter(ele=>ele._id==id)[0]
    var players = mani.player
          players.push(term)
    var obj = {
      "id":mani._id,
      "title":mani.title,
      "player":mani.player
  }
    console.log(obj)
    props.deletePlayers(obj)
    closeModal()
  }
  return (
    <div>
      <p1>TEAMS</p1>
      {props.t.team.map(ele=>{
        return(
          <div id={ele._id}>
            <Box width={1 / 4} bgcolor="grey.300" p={1} my={0.5}>
            <p>{ele.title} <button onClick={()=>props.deleteTeam(ele._id)}>Delete</button>

            <button onClick={openModal} >Add Player</button>
            <Modal isOpen={isOpen} onRequestClose={closeModal}>
              <input
                      className="main-input-style"
                      type="text"
                      value={term}
                      onChange={(event)=>setTerm(event.target.value)}
                      placeholder={`Enter Team Name`}
                  /><br/>
                <Button onClick={()=>addPlayer(ele._id)}>Add player</Button> <Button onClick={closeModal}>Cancel</Button>
              </Modal>
            </p>
            </Box>
          </div>
        )
      })}
    </div>
  );
};

function mapStateToProps(state) {
  console.log(state)
  return {
    t: state.team,
  };
}

export default connect(mapStateToProps, { fetchTeam,deleteTeam, deletePlayers })(Team);
