import React, { useEffect, useState, useCallback } from "react";
import { fetchTeam, deleteTeam, addPlayers } from "../action/index";
import { connect } from "react-redux";
import Button from '@material-ui/core/Button';
import Modal from "@netojose/react-modal";
const Team = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const openModal = useCallback(() => setIsOpen(true), []);
  const closeModal = useCallback(() => setIsOpen(false), []);
  const [term, setTerm] = useState("");
  useEffect(() => {
    props.fetchTeam();
  }, []);

  const addPlayer = (id) => {
    var mani = props.t.team.filter(ele => ele._id == id)[0]
    var players = mani.player
    players.push(term)
    var obj = {
      "id": mani._id,
      "title": mani.title,
      "player": mani.player
    }
    console.log(obj)
    props.addPlayer(obj)
    closeModal()
  }
  return (
    <div>
      {props.t.team.map(ele => {
        return (
          <div id={ele._id}>
            <p>{ele.title} <button onClick={() => props.deleteTeam(ele._id)}>Delete</button>

              <button onClick={openModal} >Add Player</button>
              <Modal isOpen={isOpen} onRequestClose={closeModal}>
                <input
                  className="main-input-style"
                  type="text"
                  value={term}
                  onChange={(event) => setTerm(event.target.value)}
                  placeholder={`Enter Team Name`}
                /><br />
                <Button onClick={(id) => {
                  var mani = props.t.team.filter(ele => ele._id == id)[0]
                  var players = mani.player
                  players.push(term)
                  var obj = {
                    "id": mani._id,
                    "title": mani.title,
                    "player": mani.player
                  }
                  console.log(obj)
                  props.addPlayer(obj)
                  closeModal()
                }}>Add player</Button> <Button onClick={closeModal}>Cancel</Button>
              </Modal>
            </p>
          </div>
        )
      })}
    </div>
  );
};

function mapStateToProps(state) {
  console.log(state)
  return {
    t: state.team,
  };
}

export default connect(mapStateToProps, { fetchTeam, deleteTeam, addPlayers })(Team);
